customScript.value:
let devices = {{local.13gzhnm7so.payload.output.devices}};
var defaultDeviceId = devices[0].id;
var defaultButtonId = "btnMakeDefault-" + defaultDeviceId;
var defaultButton = document.getElementById(defaultButtonId);

if (defaultButton) {
    defaultButton.style.display = 'none';
}

var dynamicVariables = {};




//For Loop for Manging objects on the screen

for (let i = 0; i < devices.length; i++) {
    var dynamicVariableName = "btnEdit-" + devices[i].id;
    dynamicVariables[dynamicVariableName] = "btnEdit-" + devices[i].id;

    var buttonElement = document.getElementById(dynamicVariableName);

    console.log("Dynamic variable name:", dynamicVariableName);
    console.log("Button element:", buttonElement);

    if (buttonElement) {
        // Use a closure to capture the value of i
        (function(index) {
            buttonElement.addEventListener("click", function() {
                console.log("Button clicked! " + dynamicVariables[this.id]);

                // Enable the hidden-input (nickname element)
                var nicknameElement = document.getElementById("nickname-" + devices[index].id);
                if (nicknameElement) {
                    console.log("Nickname element found:", nicknameElement);
                    nicknameElement.disabled = false; 
                    nicknameElement.classList.add("enabled");
                } else {
                    console.log("Nickname element not found");
                }

                var nicknameButton = document.getElementById("nicknameButton-" + devices[index].id);
                if (nicknameButton) {
                    console.log("Nickname button found:", nicknameButton);
                    nicknameButton.classList.remove("hidden");
                } else {
                    console.log("Nickname button not found");
                }

                var newButton = document.getElementById("nicknameSubmit-" + devices[index].id);
                if (newButton) {
                    console.log("New button found:", newButton);
                    newButton.style.display = 'flex'; // Show the button
                    newButton.disabled = false; // Enable the button
                } else {
                    console.log("New button not found");
                }
            });

            // Add event listener for nicknameSubmit-{id} button
            var submitButtonElement = document.getElementById("nicknameSubmit-" + devices[index].id);
            if (submitButtonElement) {
                submitButtonElement.addEventListener("click", function() {
                    // Get the value from the nickname-{id} input
                    var nicknameInputElement = document.getElementById("nickname-" + devices[index].id);
                    var nicknameValue = nicknameInputElement.value;

                    // Set the value of nicknameToSubmit input
                    var nicknameToSubmitInput = document.getElementById("nicknameToSubmit");
                    if (nicknameToSubmitInput) {
                        nicknameToSubmitInput.value = nicknameValue;
                    }

                    //Set the value of device ID
                    var idValue = devices[index].id;

                    // Set the value of idToSubmit input
                    var idToSubmitInput = document.getElementById("idToSubmit");
                    if (idToSubmitInput) {
                        idToSubmitInput.value = idValue;
                    }

                    // Click the nicknameSubmitBtn button
                    var nicknameSubmitBtn = document.getElementById("nicknameSubmitBtn");
                    if (nicknameSubmitBtn) {
                        nicknameSubmitBtn.click();
                    }

                     // Re-disable the hidden-input (nickname element)
                        nicknameInputElement.disabled = true;
                        nicknameInputElement.classList.remove("enabled");

                        // Hide the nicknameSubmit-{id} button
                        submitButtonElement.style.display = 'none';
                });
            } else {
                console.log("Submit button not found for", dynamicVariableName);
            }

            // Add event listener for btnMakeDefault-{id} button
            var defaultButtonElement = document.getElementById("btnMakeDefault-" + devices[index].id);
            if (defaultButtonElement) {
                defaultButtonElement.addEventListener("click", function() {

                    //Set the value of device ID
                    var idValue = devices[index].id;

                    // Set the value of idToSubmit input
                    var idToSubmitInput = document.getElementById("idToSubmit");
                    if (idToSubmitInput) {
                        idToSubmitInput.value = idValue;
                    }

                    // Click the setDefault button
                    var defaultSubmitBtn = document.getElementById("defaultSubmitBtn");
                    if (defaultSubmitBtn) {
                        defaultSubmitBtn.click();
                    }

                });
            } else {
                console.log("Submit button not found for", dynamicVariableName);
            }

            // Add event listener for btnDelete-{id} button
            var deleteButtonElement = document.getElementById("btnDelete-" + devices[index].id);
            var deleteFeedbackElement = document.getElementById("deleteFeedback");

            if (deleteButtonElement) {
                deleteButtonElement.addEventListener("click", function() {
                    if (devices.length <= 1) {
                        console.log("Cannot delete. Only one device left.");
                        this.disabled = true; // This line disables the button, you can remove it if you don't want to disable
                        deleteFeedbackElement.textContent = "Please add a new device before deleting your last remaining device";
                        return; // Exit the function to prevent further action
                    }

                    //Set the value of device ID
                    var idValue = devices[index].id;

                    // Set the value of idToSubmit input
                    var idToSubmitInput = document.getElementById("idToSubmit");
                    if (idToSubmitInput) {
                        idToSubmitInput.value = idValue;
                    }

                    // Click the deleteDevice button
                    var deleteDeviceBtn = document.getElementById("deleteDeviceBtn");
                    if (deleteDeviceBtn) {
                        deleteDeviceBtn.click();
                    }

                });
            } else {
                console.log("Delete button not found for", dynamicVariableName);
            }

        })(i);
    } else {
        console.log("Button element not found for", dynamicVariableName);
    }
}

