module.exports = a = async ({params}) => {
console.log('params: ', params);
let forcePasswordless = params.forcePasswordless;
let passwordlessAllowedTypes = params.passwordlessAllowedTypes;
var usableDevices = JSON.parse(params.usableDevices);

// Filter out devices if webAuthenSupport is false
if (!params.webAuthenSupport) {
    usableDevices = usableDevices.filter(device => {
        return device !== "SECURITY_KEY" && device !== "PLATFORM";
    });
}

// If forcePasswordless is true, filter usableDevices based on passwordlessAllowedTypes
if (forcePasswordless) {
    usableDevices = usableDevices.filter(device => {
        return passwordlessAllowedTypes.includes(device);
    });
}

return { usableDevices: usableDevices, count: usableDevices.length };


}

