const maxLength = 6;
function checkInputValid(val) {
    if (maxLength === "44") {
        if (/[a-z]{44}/.test(val)) {
            return true;
        }
    } else if (maxLength === "8") {
        if (/^[0-9]{6}$|^[0-9]{8}$/.test(val)) {
            return true;
        }
    } else {
        if (/^[0-9]{6}$/.test(val)) {
            return true;
        }
    }
    return false;
}

